mapper.update:
UPDATE t_user SET
qrcode = #{qrCode},
qrurl = #{qrUrl},
qrticket = #{qrTicket}
WHERE openid = #{openId};

mapper.select:
SELECT u.*, c.id cid, c.brand, c.model, c.color, c.carnumber
from t_user u left join t_car c on u.id=c.userid
WHERE u.id = #{id};

SELECT u.*, c.id cid, c.brand, c.model, c.color, c.carnumber
from t_user u left join t_car c on u.id=c.userid
WHERE c.carnumber = #{carNumber};

SELECT u.*, c.id cid, c.brand, c.model, c.color, c.carnumber
from t_user u left join t_car c on u.id=c.userid
WHERE u.zzid = #{zzid};

SELECT qrcode
from t_user
WHERE id = #{id}

SELECT count(*)
from t_user u
WHERE u.openid = #{openId};

SELECT u.*, c.id cid, c.brand, c.model, c.color, c.carnumber
from t_user u left join t_car c on u.id=c.userid
WHERE u.openid = #{openId};

SELECT count(*)
from t_user u
WHERE u.telephone = #{telephone};

SELECT u.*, c.id cid, c.brand, c.model, c.color, c.carnumber
from t_user u left join t_car c on u.id=c.userid
WHERE u.telephone = #{telephone};

